Schedule.DaysViewSettings.SpecialTimeRegions.Add(new SpecialTimeRegion { StartTime = new System.DateTime(2020, 12, 13, 13, 0, 0), EndTime = new System.DateTime(2020, 12, 13, 14, 0, 0), Text = "Lunch", CanEdit = false, Background = Brushes.Black, Foreground = Brushes.White, RecurrenceRule = "FREQ=DAILY;INTERVAL=1", CanMergeAdjacentRegions =false, ResourceIdCollection = new ObservableCollection<object>() { "0", "1", "2" } });
