using Syncfusion.UI.Xaml.TreeView.Engine;
class ExpandTemplateSelector : DataTemplateSelector { public DataTemplate ExpanderTemplate { get; set; } public DataTemplate CollapsedTemplate { get; set; } protected override DataTemplate SelectTemplateCore(object item, DependencyObject container) { var treeviewNode = item as TreeViewNode; if (treeviewNode == null) return null;
if (treeviewNode.IsExpanded) return ExpanderTemplate; else return CollapsedTemplate; } }

