using Syncfusion.UI.Xaml.TreeView.Engine;
class ItemTemplateSelector : DataTemplateSelector { public DataTemplate ImageTemplate { get; set; } public DataTemplate DefaultTemplate { get; set; } protected override DataTemplate SelectTemplateCore(object item, DependencyObject container) { var treeviewNode = item as TreeViewNode; if (treeviewNode == null) return null;
if (treeviewNode.IsExpanded) return ImageTemplate; else return DefaultTemplate; } }


This example shows how to enable editing in XAML and C#.


             <syncfusion:SfTreeView x:Name="treeView"
                                    AllowEditing="True">
             </syncfusion:SfTreeView>



             using Syncfusion.UI.Xaml.TreeView;

             SfTreeView sfTreeView = new SfTreeView();
             sfTreeView.AllowEditing = true;


